// Server: src/emails/sendgrid-service.ts
import sgMail from '@sendgrid/mail';
import Handlebars from 'handlebars';
import fs from 'fs';
import path from 'path';

sgMail.setApiKey(process.env.SENDGRID_API_KEY!);

const FROM_EMAIL = process.env.FROM_EMAIL || 'noreply@yourdomain.com';
const FROM_NAME = process.env.FROM_NAME || 'Your Company';

export class EmailService {
  private templates = new Map<string, HandlebarsTemplateDelegate>();

  constructor() {
    this.loadTemplates();
  }

  /**
   * Load all email templates
   */
  private loadTemplates() {
    const templatesDir = path.join(__dirname, '../../templates/emails');
    const files = fs.readdirSync(templatesDir);

    files.forEach(file => {
      if (file.endsWith('.hbs')) {
        const templateName = file.replace('.hbs', '');
        const templateContent = fs.readFileSync(
          path.join(templatesDir, file),
          'utf-8'
        );

        this.templates.set(templateName, Handlebars.compile(templateContent));
      }
    });

    console.log(`Loaded ${this.templates.size} email templates`);
  }

  /**
   * Send email using template
   */
  async sendTemplate(params: {
    to: string;
    template: string;
    subject: string;
    data: Record<string, any>;
    attachments?: { filename: string; content: Buffer; type: string }[];
  }) {
    const template = this.templates.get(params.template);

    if (!template) {
      throw new Error(`Email template not found: ${params.template}`);
    }

    const html = template(params.data);

    const message = {
      to: params.to,
      from: { email: FROM_EMAIL, name: FROM_NAME },
      subject: params.subject,
      html,
      text: this.htmlToText(html),
      attachments: params.attachments?.map(att => ({
        filename: att.filename,
        content: att.content.toString('base64'),
        type: att.type,
        disposition: 'attachment'
      }))
    };

    try {
      await sgMail.send(message);
      console.log(`Email sent to ${params.to} using template ${params.template}`);
    } catch (error: any) {
      console.error('SendGrid error:', error.response?.body || error);
      throw new Error('Email send failed');
    }
  }

  /**
   * Send welcome email
   */
  async sendWelcome(to: string, data: { name: string; verificationUrl: string }) {
    return await this.sendTemplate({
      to,
      template: 'welcome',
      subject: 'Welcome to Our Platform!',
      data
    });
  }

  /**
   * Send password reset email
   */
  async sendPasswordReset(to: string, data: { name: string; resetUrl: string; expiresIn: string }) {
    return await this.sendTemplate({
      to,
      template: 'password-reset',
      subject: 'Reset Your Password',
      data
    });
  }

  /**
   * Send order confirmation
   */
  async sendOrderConfirmation(to: string, data: {
    orderNumber: string;
    items: { name: string; quantity: number; price: number }[];
    total: number;
    shippingAddress: string;
  }) {
    return await this.sendTemplate({
      to,
      template: 'order-confirmation',
      subject: `Order Confirmation #${data.orderNumber}`,
      data
    });
  }

  /**
   * Convert HTML to plain text (basic)
   */
  private htmlToText(html: string): string {
    return html
      .replace(/<[^>]*>/g, '')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .trim();
  }
}

